import React from 'react';
import styled from '@emotion/styled';


const ContainerPhrase = styled.div`
padding:3rem;
border-radius:.5rem;
background-color:#fff;
max-width:800px;
margin-top:10rem;

`;

const Phrase = ({phrase}) => {
    return (  
    <ContainerPhrase>
    <h1>{phrase.quote}</h1>
    <p>{phrase.author}</p>
    </ContainerPhrase>
    );
}
 
export default Phrase;
